  // Récupération des livres (avec info utilisateur)
  try {
      $pdo = new PDO('mysql:host=localhost;dbname=bookshare_php;charset=utf8mb4', 'root', '', [
          PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
          PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC
      ]);

      $sql = "
        SELECT
          b.*,
          b.image AS cover_path,
          u.name AS user_name,
          u.avatar AS user_photo
        FROM books b
        LEFT JOIN users u ON b.owner_id = u.id
        ORDER BY b.created_at DESC
      ";
      $stmt = $pdo->query($sql);
      $books = $stmt->fetchAll();
  } catch (PDOException $e) {
      // en prod logguer l'erreur, ici on affiche un tableau vide
      $books = [];
  }

  function resolve_public_image(string $path): string {
      $path = trim($path);
      if ($path === '') return '../pics/11.webp';

      // si c'est déjà une URL complète
      if (preg_match('#^https?://#i', $path)) return $path;

      // s'assurer qu'il commence par /
      $webPath = '/' . ltrim($path, '/');

      // vérifier l'existence dans le document root
      if (!empty($_SERVER['DOCUMENT_ROOT']) && file_exists($_SERVER['DOCUMENT_ROOT'] . $webPath)) {
          return $webPath;
      }

      // sinon essayer en partant du dossier courant (ex: public/)
      $candidate = __DIR__ . '/../' . ltrim($path, '/');
      if (file_exists($candidate)) {
          // construire un chemin web relatif depuis la racine si possible
          $rel = '/' . ltrim($path, '/');
          return $rel;
      }

      // fallback
      return '/img/placeholder.png';
  }
