<script>
let currentChat = { bookId: null, currentUserId: null, receiverId: null };

function openChat(bookId, currentUserId, receiverId) {
  currentChat = { bookId, currentUserId, receiverId };
  document.getElementById('chat-popup').style.display = 'block';
  document.getElementById('chat-messages').innerHTML = '';
  initChat(bookId, currentUserId, receiverId);
}

function closeChat() {
  document.getElementById('chat-popup').style.display = 'none';
}

function sendMessage() {
  const input = document.getElementById('message-input');
  const msg = input.value.trim();
  if (msg) {
    // Emit via socket or handle as per chat.js
    if (typeof socket !== 'undefined') {
      socket.emit('chat message', { bookId: currentChat.bookId, senderId: currentChat.currentUserId, receiverId: currentChat.receiverId, message: msg });
    }
    input.value = '';
  }
}
</script>
